"use strict";

var generateTypeFilters = require('./generateTypeFilters');

var generateTypeSortings = require('./generateTypeSortings');

var generateTypeQueries = require('./generateTypeQueries');

module.exports = extracted => {
  var types = [...extracted.types, extracted.interfaces.find(i => i.name === 'Document')];
  var filters = generateTypeFilters(types);
  var sortings = generateTypeSortings(types);
  var queries = generateTypeQueries(types, sortings);
  var graphqlTypes = extracted.types.concat(filters).concat(sortings);
  return {
    types: graphqlTypes,
    queries,
    interfaces: extracted.interfaces,
    generation: 'gen3'
  };
};