"use strict";

var resolveProjectRoot = require('@sanity/resolver').resolveProjectRoot;

var webpackIntegration = require('@sanity/webpack-integration/v3');

var isSanityMonorepo = require('./isSanityMonorepo');

var basePath = resolveProjectRoot({
  sync: true
});
module.exports = {
  plugins: webpackIntegration.getPostcssPlugins({
    basePath,
    isSanityMonorepo: isSanityMonorepo(basePath),
    cssnext: {
      features: {
        customProperties: true
      }
    }
  })
};