"use strict";

/* eslint-disable no-process-env */
var getStaticBasePath = config => {
  if (!process.env.STUDIO_BASEPATH && (!config.project || !config.project.basePath)) {
    return '/static';
  }

  var basePath = (process.env.STUDIO_BASEPATH || config.project && config.project.basePath || '').replace(/\/+$/, '');
  return "".concat(basePath, "/static");
};

module.exports = getStaticBasePath;